ChangeGVBuffer.Script:
var str=data.getObject();
if (str == "gianfranco" || str == "GiAnFrAnCo") {
    Thread.sleep(2000);
    data.setProperty("PIPPO", null);
}
if (str == "ciro" || str == "CiRo") {
    Thread.sleep(5000);
    data.setObject("CIRO");
}
if (str == "antonio" || str == "AnToNiO") {
    Thread.sleep(10000);
}
if (str == "nunzio" || str == "NuNzIo") {
    Thread.sleep(3000);
    data.setObject("NUNZIO");
}

